// Mostrar stats de EA en producto individual
add_action('woocommerce_single_product_summary', 'doittrading_show_ea_stats_acf', 25);
function doittrading_show_ea_stats_acf() {
    // Obtener el producto actual
    $product_id = get_the_ID();

    // Verificar que estamos en un producto
    if (!$product_id || get_post_type($product_id) !== 'product') {
        return;
    }

    // Solo mostrar si tiene datos ACF
    if (get_field('monthly_gain', $product_id)) {
        ?>
        <div class="doittrading-ea-stats">
            <h3>Performance Stats</h3>
            <div class="stats-grid">
                <div class="stat-box">
                    <span class="stat-label">Monthly Gain</span>
                    <span class="stat-value positive">+<?php the_field('monthly_gain', $product_id); ?>%</span>
                </div>
                <div class="stat-box">
                    <span class="stat-label">Win Rate</span>
                    <span class="stat-value"><?php the_field('win_rate', $product_id); ?>%</span>
                </div>
                <div class="stat-box">
                    <span class="stat-label">Max Drawdown</span>
                    <span class="stat-value negative">-<?php the_field('max_drawdown', $product_id); ?>%</span>
                </div>
                <div class="stat-box">
                    <span class="stat-label">Profit Factor</span>
                    <span class="stat-value"><?php the_field('profit_factor', $product_id); ?></span>
                </div>
            </div>

            <?php if (get_field('supported_platforms', $product_id)): ?>
            <div class="platforms-badges">
                <?php 
                $platforms = get_field('supported_platforms', $product_id);
                foreach($platforms as $platform): ?>
                    <span class="platform-badge"><?php echo strtoupper($platform); ?></span>
                <?php endforeach; ?>
            </div>
            <?php endif; ?>
        </div>
        <?php
    }
}
