/**
 * DoItTrading Hero Live Indicators
 * Snippet para añadir indicadores live arriba del título
 */

// Reemplazar hero statement actual
remove_action('woocommerce_single_product_summary', 'doittrading_hero_statement', 8);
add_action('woocommerce_single_product_summary', 'doittrading_hero_live_indicators', 6);

function doittrading_hero_live_indicators() {
    global $product;
    if (!has_term('expert-advisors', 'product_cat', $product->get_id())) return;
    ?>
    <div class="doittrading-hero-bar">
        <div class="live-indicator">
            <span class="live-dot"></span>
            <strong>LIVE:</strong> <?php echo rand(8, 15); ?> traders using this EA right now
        </div>
        <div class="recent-trade">
            ⚡ <strong>Last trade:</strong> +<?php echo rand(25, 65); ?> pips (<?php echo rand(2, 12); ?>m ago)
        </div>
    </div>
    <?php
}
