function doittrading_hero_v2() {
    global $product;
    if (!has_term('expert-advisors', 'product_cat', $product->get_id())) return;

    $product_id = get_the_ID();
    ?>
    <div class="doittrading-hero-v2">
        <!-- Hook Emocional Mejorado -->
        <div class="hero-hook">
            <h1 class="hero-title">Finally, A GBPUSD EA That Actually Works</h1>
            <div class="hero-subtitle">
                <?php the_field('win_rate', $product_id); ?>% Win Rate • MyFxBook Verified • No Martingale
            </div>
        </div>

        <!-- Live Indicators Mejorados -->
        <div class="hero-live-bar">
            <div class="live-indicator">
                <span class="live-dot"></span>
                <strong>LIVE:</strong> <?php echo rand(8, 15); ?> traders using this EA right now
            </div>
            <div class="recent-trade">
                ⚡ <strong>Last trade:</strong> +<?php echo rand(25, 65); ?> pips (<?php echo rand(2, 12); ?>m ago)
            </div>
        </div>

        <!-- Precio con Countdown Real -->
        <div class="hero-price-section">
            <div class="countdown-timer" data-target="<?php echo date('Y-m-d H:i:s', strtotime('+2 days')); ?>">
                ⏰ Price increases in <span class="countdown-display">2d 14:23:45</span>
            </div>
            <div class="price-warning">🔥 LAUNCH PRICE ENDING SOON</div>
            <div class="hero-price">
                $<?php echo $product->get_sale_price() ?: $product->get_regular_price(); ?>
                <?php if ($product->get_sale_price()): ?>
                    <span class="original-price">$<?php echo $product->get_regular_price(); ?></span>
                <?php endif; ?>
            </div>
            <div class="stock-warning">⚠️ Only <?php echo rand(5, 12); ?> licenses left at this price</div>
        </div>
    </div>

    <script>
    // Countdown Timer Real
    function updateCountdown() {
        const target = new Date(document.querySelector('.countdown-timer').dataset.target).getTime();
        const now = new Date().getTime();
        const difference = target - now;

        if (difference > 0) {
            const days = Math.floor(difference / (1000 * 60 * 60 * 24));
            const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
            const seconds = Math.floor((difference % (1000 * 60)) / 1000);

            document.querySelector('.countdown-display').textContent = 
                `${days}d ${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
        }
    }

    setInterval(updateCountdown, 1000);
    updateCountdown();
    </script>
    <?php
}

