function doittrading_roi_calculator() {
    global $product;
    $product_id = get_the_ID();
    $monthly_gain = get_field('monthly_gain', $product_id);
    $price = $product->get_price();

    if (!$monthly_gain || !$price) return;

    // Cálculos ROI (basado en $1000 de capital inicial)
    $capital = 1000;
    $monthly_return = ($monthly_gain / 100) * $capital;
    $payback_months = $price / $monthly_return;
    $yearly_profit = $monthly_return * 12;
    $roi_percentage = (($yearly_profit / $price) * 100);
    ?>
    <div class="roi-calculator-section">
        <h3>💰 ROI Calculator: See Your Real Returns</h3>
        <div class="roi-grid">
            <div class="roi-item">
                <div class="roi-value">$<?php echo number_format($price); ?></div>
                <div class="roi-label">EA Cost</div>
            </div>
            <div class="roi-item">
                <div class="roi-value">$<?php echo number_format($monthly_return); ?></div>
                <div class="roi-label">Monthly Return<br><small>(<?php echo $monthly_gain; ?>% on $<?php echo number_format($capital); ?>)</small></div>
            </div>
            <div class="roi-item">
                <div class="roi-value"><?php echo number_format($payback_months, 1); ?></div>
                <div class="roi-label">Months to<br>Pay for Itself</div>
            </div>
            <div class="roi-item">
                <div class="roi-value"><?php echo number_format($roi_percentage); ?>%</div>
                <div class="roi-label">Year 1 ROI<br><small>($<?php echo number_format($yearly_profit); ?> profit)</small></div>
            </div>
        </div>
        <p class="roi-disclaimer">*Based on verified 6-month performance. Past results don't guarantee future returns.</p>
    </div>
    <?php
}
