// Hero Statement
function doittrading_hero_statement() {
    global $product;
    if (!has_term('expert-advisors', 'product_cat', $product->get_id())) return;
    ?>
    <div class="hero-statement">
        <div class="alert-success">
            ⚡ <strong>ACTIVE NOW:</strong> <?php echo rand(3, 8); ?> traders running this EA
        </div>
    </div>
    <?php
}
add_action('woocommerce_single_product_summary', 'doittrading_hero_statement', 8);

