INSERT INTO `wp_snippets` VALUES (7,'DoItTrading Why Us Section','','// Sección más honesta y profesional\nfunction doittrading_why_us_section() {\n    if (!has_term(\'expert-advisors\', \'product_cat\')) return;\n    ?>\n    <div class=\"why-doittrading\">\n        <h2>Why Choose DoItTrading EAs</h2>\n        <div class=\"features-grid\">\n            <div class=\"feature\">\n                <div class=\"icon\">📈</div>\n                <h3>Transparent Results</h3>\n                <p>All our EAs have public MyFxBook accounts. Check real performance before buying.</p>\n            </div>\n            <div class=\"feature\">\n                <div class=\"icon\">🛠️</div>\n                <h3>Professional Support</h3>\n                <p>Get help with setup and optimization. We respond within 24 hours.</p>\n            </div>\n            <div class=\"feature\">\n                <div class=\"icon\">📚</div>\n                <h3>Detailed Documentation</h3>\n                <p>Complete guides and best practices included with every EA.</p>\n            </div>\n            <div class=\"feature\">\n                <div class=\"icon\">🔄</div>\n                <h3>Active Development</h3>\n                <p>Regular updates based on market conditions and user feedback.</p>\n            </div>\n        </div>\n    </div>\n    <?php\n}\nadd_action(\'woocommerce_after_single_product_summary\', \'doittrading_why_us_section\', 20);','','global',10,0,'2025-07-18 20:09:49',6,NULL);
INSERT INTO `wp_snippets` VALUES (8,'DoItTrading Product FAQs','','// FAQs específicas por producto\nfunction doittrading_product_faqs() {\n    $product_id = get_the_ID();\n    \n    // Verificar si hay FAQs\n    if (!get_field(\'faq_question_1\', $product_id)) return;\n    ?>\n    <div class=\"product-faqs\">\n        <h2>Frequently Asked Questions</h2>\n        \n        <?php for($i = 1; $i <= 3; $i++): \n            $question = get_field(\'faq_question_\' . $i, $product_id);\n            $answer = get_field(\'faq_answer_\' . $i, $product_id);\n            if ($question && $answer): ?>\n                <div class=\"faq-item\">\n                    <h3><?php echo esc_html($question); ?></h3>\n                    <p><?php echo esc_html($answer); ?></p>\n                </div>\n            <?php endif;\n        endfor; ?>\n    </div>\n    <?php\n}\nadd_action(\'woocommerce_after_single_product_summary\', \'doittrading_product_faqs\', 25);','','global',10,0,'2025-07-18 20:09:25',2,NULL);
