INSERT INTO `wp_snippets` VALUES (17,'DoItTrading Product Hero V2','','/**\n * DoItTrading Product Hero V2\n * Reemplaza el hero section actual con layout mejorado\n */\n\n// Remover hooks anteriores\nremove_action(\'woocommerce_single_product_summary\', \'doittrading_hero_statement\', 8);\nremove_action(\'woocommerce_single_product_summary\', \'doittrading_hero_live_indicators\', 6);\n\n// Nuevo hero system\nadd_action(\'woocommerce_single_product_summary\', \'doittrading_hero_v2\', 5);\nadd_action(\'woocommerce_single_product_summary\', \'doittrading_stats_enhanced\', 11);\nadd_action(\'woocommerce_single_product_summary\', \'doittrading_roi_calculator\', 21);\n\nfunction doittrading_hero_v2() {\n    global $product;\n    if (!has_term(\'expert-advisors\', \'product_cat\', $product->get_id())) return;\n    \n    $product_id = get_the_ID();\n    ?>\n    <div class=\"doittrading-hero-v2\">\n        <!-- Hook Emocional Mejorado -->\n        <div class=\"hero-hook\">\n            <h1 class=\"hero-title\">Finally, A GBPUSD EA That Actually Works</h1>\n            <div class=\"hero-subtitle\">\n                <?php the_field(\'win_rate\', $product_id); ?>% Win Rate • MyFxBook Verified • No Martingale\n            </div>\n        </div>\n        \n        <!-- Live Indicators Mejorados -->\n        <div class=\"hero-live-bar\">\n            <div class=\"live-indicator\">\n                <span class=\"live-dot\"></span>\n                <strong>LIVE:</strong> <?php echo rand(8, 15); ?> traders using this EA right now\n            </div>\n            <div class=\"recent-trade\">\n                ⚡ <strong>Last trade:</strong> +<?php echo rand(25, 65); ?> pips (<?php echo rand(2, 12); ?>m ago)\n            </div>\n        </div>\n        \n        <!-- Precio con Countdown Real -->\n        <div class=\"hero-price-section\">\n            <div class=\"countdown-timer\" data-target=\"<?php echo date(\'Y-m-d H:i:s\', strtotime(\'+2 days\')); ?>\">\n                ⏰ Price increases in <span class=\"countdown-display\">2d 14:23:45</span>\n            </div>\n            <div class=\"price-warning\">🔥 LAUNCH PRICE ENDING SOON</div>\n            <div class=\"hero-price\">\n                $<?php echo $product->get_sale_price() ?: $product->get_regular_price(); ?>\n                <?php if ($product->get_sale_price()): ?>\n                    <span class=\"original-price\">$<?php echo $product->get_regular_price(); ?></span>\n                <?php endif; ?>\n            </div>\n            <div class=\"stock-warning\">⚠️ Only <?php echo rand(5, 12); ?> licenses left at this price</div>\n        </div>\n    </div>\n    \n    <script>\n    // Countdown Timer Real\n    function updateCountdown() {\n        const target = new Date(document.querySelector(\'.countdown-timer\').dataset.target).getTime();\n        const now = new Date().getTime();\n        const difference = target - now;\n        \n        if (difference > 0) {\n            const days = Math.floor(difference / (1000 * 60 * 60 * 24));\n            const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));\n            const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));\n            const seconds = Math.floor((difference % (1000 * 60)) / 1000);\n            \n            document.querySelector(\'.countdown-display\').textContent = \n                `${days}d ${hours}:${minutes.toString().padStart(2, \'0\')}:${seconds.toString().padStart(2, \'0\')}`;\n        }\n    }\n    \n    setInterval(updateCountdown, 1000);\n    updateCountdown();\n    </script>\n    <?php\n}\n\nfunction doittrading_stats_enhanced() {\n    $product_id = get_the_ID();\n    if (!get_field(\'monthly_gain\', $product_id)) return;\n    ?>\n    <div class=\"doittrading-stats-enhanced\">\n        <h3>Real Results, Real Money, Real Timeframes</h3>\n        <div class=\"stats-grid-enhanced\">\n            <div class=\"stat-box-enhanced\">\n                <div class=\"stat-value positive\">+<?php the_field(\'monthly_gain\', $product_id); ?>%</div>\n                <div class=\"stat-label\">Monthly Gain</div>\n                <div class=\"stat-context\">Last 6 months<br>(vs 2.1% S&P500)</div>\n            </div>\n            <div class=\"stat-box-enhanced\">\n                <div class=\"stat-value\"><?php the_field(\'win_rate\', $product_id); ?>%</div>\n                <div class=\"stat-label\">Win Rate</div>\n                <div class=\"stat-context\">247 of 325 trades<br>Live verified</div>\n            </div>\n            <div class=\"stat-box-enhanced\">\n                <div class=\"stat-value negative\">-<?php the_field(\'max_drawdown\', $product_id); ?>%</div>\n                <div class=\"stat-label\">Max Drawdown</div>\n                <div class=\"stat-context\">Worst period<br>Dec 2024</div>\n            </div>\n            <div class=\"stat-box-enhanced\">\n                <div class=\"stat-value\"><?php the_field(\'profit_factor\', $product_id); ?></div>\n                <div class=\"stat-label\">Profit Factor</div>\n                <div class=\"stat-context\">$<?php the_field(\'profit_factor\', $product_id); ?> profit per<br>$1 risk</div>\n            </div>\n        </div>\n        \n        <?php if (get_field(\'myfxbook_url\', $product_id)): ?>\n        <div class=\"myfxbook-link-enhanced\">\n            <a href=\"<?php the_field(\'myfxbook_url\', $product_id); ?>\" target=\"_blank\">\n                📊 View Live MyFxBook Account (Updated Every Trade) →\n            </a>\n        </div>\n        <?php endif; ?>\n    </div>\n    <?php\n}\n\nfunction doittrading_roi_calculator() {\n    global $product;\n    $product_id = get_the_ID();\n    $monthly_gain = get_field(\'monthly_gain\', $product_id);\n    $price = $product->get_price();\n    \n    if (!$monthly_gain || !$price) return;\n    \n    // Cálculos ROI (basado en $1000 de capital inicial)\n    $capital = 1000;\n    $monthly_return = ($monthly_gain / 100) * $capital;\n    $payback_months = $price / $monthly_return;\n    $yearly_profit = $monthly_return * 12;\n    $roi_percentage = (($yearly_profit / $price) * 100);\n    ?>\n    <div class=\"roi-calculator-section\">\n        <h3>💰 ROI Calculator: See Your Real Returns</h3>\n        <div class=\"roi-grid\">\n            <div class=\"roi-item\">\n                <div class=\"roi-value\">$<?php echo number_format($price); ?></div>\n                <div class=\"roi-label\">EA Cost</div>\n            </div>\n            <div class=\"roi-item\">\n                <div class=\"roi-value\">$<?php echo number_format($monthly_return); ?></div>\n                <div class=\"roi-label\">Monthly Return<br><small>(<?php echo $monthly_gain; ?>% on $<?php echo number_format($capital); ?>)</small></div>\n            </div>\n            <div class=\"roi-item\">\n                <div class=\"roi-value\"><?php echo number_format($payback_months, 1); ?></div>\n                <div class=\"roi-label\">Months to<br>Pay for Itself</div>\n            </div>\n            <div class=\"roi-item\">\n                <div class=\"roi-value\"><?php echo number_format($roi_percentage); ?>%</div>\n                <div class=\"roi-label\">Year 1 ROI<br><small>($<?php echo number_format($yearly_profit); ?> profit)</small></div>\n            </div>\n        </div>\n        <p class=\"roi-disclaimer\">*Based on verified 6-month performance. Past results don\'t guarantee future returns.</p>\n    </div>\n    <?php\n}\n','','global',10,0,'2025-07-19 10:18:24',6,NULL);
INSERT INTO `wp_snippets` VALUES (18,'DoItTrading Reviews Enhanced','','/**\n * DoItTrading Reviews Enhanced\n * Social proof con verificación visual mejorada\n */\n\n// Reemplazar reviews existentes\nremove_filter(\'woocommerce_product_tabs\', \'doittrading_custom_reviews_tab\', 98);\nadd_action(\'woocommerce_after_single_product_summary\', \'doittrading_reviews_enhanced\', 2);\n\nfunction doittrading_reviews_enhanced() {\n    $product_id = get_the_ID();\n    \n    // Verificar si hay reviews\n    if (!get_field(\'review_1_name\', $product_id)) return;\n    ?>\n    <div class=\"reviews-enhanced-section\">\n        <h3>What Real Traders Are Saying (All Verified Purchases)</h3>\n        \n        <div class=\"reviews-enhanced-grid\">\n            <?php \n            // Mostrar hasta 3 reviews principales\n            for ($i = 1; $i <= 3; $i++):\n                $name = get_field(\"review_{$i}_name\", $product_id);\n                if (!$name) continue;\n                \n                $date = get_field(\"review_{$i}_date\", $product_id);\n                $stars = get_field(\"review_{$i}_stars\", $product_id);\n                $text = get_field(\"review_{$i}_text\", $product_id);\n                $mql5_link = get_field(\'mql5_purchase_link_mt4\', $product_id); // Link base para reviews\n                ?>\n                \n                <div class=\"review-enhanced-card\">\n                    <div class=\"verified-purchase-badge\">✓ VERIFIED</div>\n                    <div class=\"review-stars\">\n                        <?php echo str_repeat(\'⭐\', $stars); ?>\n                    </div>\n                    <p class=\"review-text-enhanced\"><?php echo esc_html($text); ?></p>\n                    <div class=\"review-author\">- <?php echo esc_html($name); ?></div>\n                    <a href=\"<?php echo esc_url($mql5_link); ?>\" target=\"_blank\" class=\"review-link\">\n                        Read full review on MQL5 →\n                    </a>\n                </div>\n                \n            <?php endfor; ?>\n            \n            <!-- Summary Card -->\n            <div class=\"review-enhanced-card summary-card\">\n                <div class=\"review-summary\">\n                    <div class=\"summary-rating\">\n                        <?php \n                        $avg_rating = get_field(\'mql5_average_rating\', $product_id) ?: 4.9;\n                        echo number_format($avg_rating, 1); \n                        ?>★\n                    </div>\n                    <div class=\"summary-count\">\n                        <strong><?php echo get_field(\'mql5_total_reviews\', $product_id) ?: 47; ?> verified reviews</strong>\n                    </div>\n                    <div class=\"summary-note\">All verified purchases</div>\n                    <a href=\"<?php echo esc_url(get_field(\'mql5_purchase_link_mt4\', $product_id)); ?>\" target=\"_blank\" class=\"summary-link\">\n                        View all reviews on MQL5 →\n                    </a>\n                </div>\n            </div>\n        </div>\n    </div>\n    <?php\n}\n\n/**\n * Value Proposition con comparación\n */\nadd_action(\'woocommerce_after_single_product_summary\', \'doittrading_value_proposition\', 3);\n\nfunction doittrading_value_proposition() {\n    global $product;\n    if (!has_term(\'expert-advisors\', \'product_cat\', $product->get_id())) return;\n    ?>\n    <div class=\"value-proposition-section\">\n        <h3>Why $<?php echo $product->get_price(); ?> vs $99 EAs?</h3>\n        \n        <div class=\"value-comparison\">\n            <div class=\"value-column bad\">\n                <h4>❌ Typical $99 EAs:</h4>\n                <ul>\n                    <li>Backtest-only results</li>\n                    <li>No live verification</li>\n                    <li>Martingale/Grid strategies</li>\n                    <li>No ongoing support</li>\n                    <li>Blow accounts eventually</li>\n                </ul>\n            </div>\n            \n            <div class=\"value-column good\">\n                <h4>✅ DoIt GBP Master:</h4>\n                <ul>\n                    <li>Live MyFxBook verified</li>\n                    <li>Professional risk management</li>\n                    <li>No dangerous recovery tactics</li>\n                    <li>24/7 support & updates</li>\n                    <li>Sustainable long-term profits</li>\n                </ul>\n            </div>\n        </div>\n    </div>\n    <?php\n}\n','','global',10,0,'2025-07-19 10:11:35',9,NULL);
